<script>
export default {
  name: 'Home',
  data () {
    return {
      loading: false,
      startY: 0,
      timer: null,
      touchStatus: false,
      dialog: false,
      list: [{},{},{},{},{},{},{},{},{},{}],
      dataList: []
    }
  },
  mounted() {
    this.getJson()
  },
  methods: {
    getJson() {
      this.loading = true
      let data = JSON.parse(localStorage.getItem("data"))
      data.forEach((item, index) => {
        this.dataList.push({})
        this.dataList[index].amount = item.amount
        this.dataList[index].id = item.id
        this.dataList[index].share = item.share
        this.dataList[index].time = item.time
        this.dataList[index].userId = item.user_id
      })

      this.getList()
    },
    getList() {
      this.loading = true
      setTimeout(() =>{
        let length = this.dataList.length
        if(length > 10){
          if(!this.list[0].id){
            this.list = []
          }
          this.dataList.forEach((item, index) => {
            if(index <= 9){
              this.list.push(item)
            }
          })
          this.dataList.splice(0, 10)
          this.loading = false
        } else if(!this.list[0].id){
          this.dataList.forEach((item, index) => {
            this.list[index] = item
          })
          this.dataList = []
          this.loading = false
        } else {
          this.dataList.forEach(item => {
            this.list.push(item)
          })
          this.dataList = []
          this.loading = false
        }
      }, 1000)
    },
    handleTouchEnd (e) {
      this.touchStatus = false
      let diff = e.changedTouches[0].pageY - this.startY
      if(diff < 0  && this.dataList.length){
        this.getList()
      }
    },
    handleTouchStart (e) {
      this.startY = 0
      this.touchStatus = true
      this.startY = e.changedTouches[0].pageY
    },
    doLoadMore() {
      console.log('LoadMore')
    },
    showDialog() {
      this.dialog = true
    },
    hideDialog() {
      this.dialog = false
    },
  }
}
</script>

